.checkbox-toggle {
	position: relative;

	input{
		display:block;
		position:absolute;
		top:0;
		right:0;
		bottom:0;
		left:0;
		width:0%;
		height:0%;
		margin:0 0;
		cursor:pointer;
		.opacity(0);


		+ span {
			cursor:pointer;
			user-select: none;

			&:before {
				position:absolute;
				left:0px;
				display:inline-block;
			}

			> h4 {
				display:inline;
			}
		}
	}
}

/*******************************************************
Main Slider
*******************************************************/

.checkbox-slider {

	.checkbox-toggle;

	input{

		+ span {
			padding-left:@scrubber-width-default * 2;

			&:before {
				content:"";
				height:@scrubber-width-default;
				width:@scrubber-width-default * 2;
				background:rgba(100,100,100,.2);
				box-shadow:inset 0 0 5px rgba(0,0,0,.8);
				transition: background .2s ease-out;
			}
			// Off State
			&:after {
				width:@scrubber-width-default;
				height:@scrubber-width-default;
				position:absolute;
				left:0px;
				top:0;
				display:block;
				background:@white;
				transition: margin-left 0.1s ease-in-out;
				text-align:center;
				font-weight:bold;
				content:"";
			}
		}
		// On state
		&:checked + span:after {
			margin-left: @scrubber-width-default;
			content:"";
		}
		&:checked + span:before {
			transition: background .2s ease-in;
		}

	}

}


/*******************************************************
Slider default
*******************************************************/

.checkbox-slider--default {

	.checkbox-slider;

	input + span:after {
		background:@white;
		border:solid transparent 1px;
		background-clip:content-box;
	}

	input:checked + span:after {

		background:@brand-success;
		border:solid transparent 1px;
		background-clip:content-box;
	}

}

